mapper.select:
	  SELECT DISTINCT A.PRODUCTCATEGORY
    FROM MDWMGR.DAS_OPDEFECT_MWD A
   WHERE     A.FACTORYNAME = #{shopName}
         AND A.OPER_DESC = #{operationName}
         AND A.PRODUCTIONTYPE = #{productionType}
ORDER BY A.PRODUCTCATEGORY ASC

	  SELECT DISTINCT A.PRODUCTCATEGORY
    FROM MDWMGR.DAS_MMDDEFECT_MWD A
   WHERE     A.FACTORYNAME = #{shopName}
         AND A.OPER_DESC = #{operationName}
         AND A.PRODUCTIONTYPE = #{productionType}
ORDER BY A.PRODUCTCATEGORY ASC

		  SELECT DISTINCT DESCRIPTION
    FROM MDWMGR.DAS_OPDEFECT_MWD A
      WHERE     1 = 1
         AND A.FACTORYNAME = #{shopName}
         AND A.OPER_DESC = #{operationName}
         AND A.PRODUCTIONTYPE = #{productionType}
         AND A.PRODUCTCATEGORY = #{productCategory}
         AND A.TIMEHH >= #{startTime}
         AND A.TIMEHH <= #{endTime}
         AND A.DESCRIPTION != '-'
ORDER BY A.DESCRIPTION

SELECT B.FACTORYNAME, B.OPER_DESC, B.PRODUCTCATEGORY, B.PRODUCTIONTYPE, B.DESCRIPTION, B.DEFECTQTY, B.OUTQTY, CASE WHEN D.OUTQTY = 0 THEN 0 WHEN B.DEFECTQTY = 0 THEN 0 ELSE TRUNC ( (B.DEFECTQTY / D.OUTQTY) * 100, 2) END AS DEFECTRATIO, D.OUTQTY AS OUTQTY_1 FROM ( SELECT TT.FACTORYNAME, TT.OPER_DESC, TT.PRODUCTCATEGORY, TT.PRODUCTIONTYPE, TT.DESCRIPTION, SUM (TT.DEFECTQTY) AS DEFECTQTY, SUM (TT.OUTQTY) AS OUTQTY, SUM (TT.DEFECTRATIO) AS DEFECTRATIO FROM ( SELECT A.OPERATORID, A.FACTORYNAME, A.FACTORYDATE, A.TIMEHH, A.OPER_DESC, A.PRODUCTSPECNAME, A.PRODUCTCATEGORY, A.PRODUCTIONTYPE, A.DEFECTCODE, A.DESCRIPTION, A.DEFECTQTY, A.OUTQTY, A.DEFECTRATIO * 100 AS DEFECTRATIO, A.DEFECTRATIOPK, A.ETLTIME FROM MDWMGR.DAS_OPDEFECT_MWD A WHERE A.FACTORYNAME = #{shopName} AND A.OPER_DESC = #{operationName} AND A.PRODUCTIONTYPE = #{productionType} AND A.PRODUCTCATEGORY = #{productCategory}
AND A.DESCRIPTION=#{defectCode}
--AND A.DEFECTRATIOPK <=5
AND ( ( A.TIMEHH <= #{endTime} AND A.TIMEHH >= #{startTime}) ) ORDER BY OPERATORID) TT GROUP BY TT.DESCRIPTION, TT.FACTORYNAME, TT.OPER_DESC, TT.PRODUCTCATEGORY, TT.PRODUCTIONTYPE ORDER BY TT.DESCRIPTION DESC) B, ( SELECT TT1.FACTORYNAME, SUM (TT1.OUTQTY) AS OUTQTY FROM ( SELECT C.OPERATORID, C.FACTORYNAME, C.FACTORYDATE, C.TIMEHH, C.OPER_DESC, C.PRODUCTSPECNAME, C.PRODUCTCATEGORY, C.PRODUCTIONTYPE, MAX (TO_NUMBER(C.OUTQTY)) AS OUTQTY FROM MDWMGR.DAS_OPDEFECT_MWD C WHERE C.FACTORYNAME = #{shopName} AND C.OPER_DESC = #{operationName} AND C.PRODUCTIONTYPE = #{productionType} AND C.PRODUCTCATEGORY = #{productCategory} --AND C.DEFECTRATIOPK <=5 AND ( ( C.TIMEHH <= #{endTime} AND C.TIMEHH >= #{startTime})) GROUP BY C.OPERATORID, C.FACTORYNAME, C.OPER_DESC, C.PRODUCTSPECNAME, C.FACTORYDATE, C.TIMEHH, C.PRODUCTCATEGORY, C.PRODUCTIONTYPE ORDER BY OPERATORID) TT1 GROUP BY TT1.FACTORYNAME) D

SELECT CASE WHEN OP.OPERATORNAME IS NULL THEN TT2.OPERATORID ELSE OP.OPERATORNAME END OPERATORID, TT2.FACTORYNAME, TT2.OPER_DESC, TT2.PRODUCTCATEGORY, TT2.PRODUCTIONTYPE, TT2.DESCRIPTION, TT2.DEFECTQTY, TT2.OUTQTY,TT2.DEFECTRATIO,TT2.OUTQTY_1 FROM (SELECT B.OPERATORID, B.FACTORYNAME, B.OPER_DESC, B.PRODUCTCATEGORY, B.PRODUCTIONTYPE, B.DESCRIPTION, B.DEFECTQTY, B.OUTQTY, CASE WHEN D.OUTQTY = 0 THEN 0 WHEN B.DEFECTQTY = 0 THEN 0 WHEN B.OPERATORID != 'SUM' THEN TRUNC ( (B.DEFECTQTY / D.OUTQTY) * 100, 2) WHEN B.OPERATORID = 'SUM' THEN TRUNC ( (B.DEFECTQTY / B.OUTQTY) * 100, 2) END AS DEFECTRATIO, D.OUTQTY AS OUTQTY_1 FROM ( SELECT TT.OPERATORID, TT.FACTORYNAME, TT.OPER_DESC, TT.PRODUCTCATEGORY, TT.PRODUCTIONTYPE, TT.DESCRIPTION, SUM (TT.DEFECTQTY) AS DEFECTQTY, SUM (TT.OUTQTY) AS OUTQTY, SUM (TT.DEFECTRATIO) AS DEFECTRATIO FROM ( SELECT A.OPERATORID, A.FACTORYNAME, A.FACTORYDATE, A.TIMEHH, A.OPER_DESC, A.PRODUCTSPECNAME, A.PRODUCTCATEGORY, A.PRODUCTIONTYPE, A.DEFECTCODE, A.DESCRIPTION, A.DEFECTQTY, A.OUTQTY, A.DEFECTRATIO * 100 AS DEFECTRATIO, A.DEFECTRATIOPK, A.ETLTIME FROM MDWMGR.DAS_OPDEFECT_MWD A WHERE A.FACTORYNAME = #{shopName} AND A.OPER_DESC = #{operationName} AND A.PRODUCTIONTYPE = #{productionType} AND A.PRODUCTCATEGORY = #{productCategory}
AND A.DESCRIPTION=#{defectCode}
--AND A.DEFECTRATIOPK <=5
AND ( ( A.TIMEHH <= #{endTime} AND A.TIMEHH >= #{startTime}) ) ORDER BY OPERATORID) TT GROUP BY TT.DESCRIPTION, TT.OPERATORID, TT.FACTORYNAME, TT.OPER_DESC, TT.PRODUCTCATEGORY, TT.PRODUCTIONTYPE ORDER BY TT.OPERATORID DESC) B, ( SELECT TT1.OPERATORID, SUM (TT1.OUTQTY) AS OUTQTY FROM ( SELECT C.OPERATORID, C.FACTORYNAME, C.FACTORYDATE, C.TIMEHH, C.OPER_DESC, C.PRODUCTSPECNAME, C.PRODUCTCATEGORY, C.PRODUCTIONTYPE, MAX (TO_NUMBER(C.OUTQTY)) AS OUTQTY FROM MDWMGR.DAS_OPDEFECT_MWD C WHERE C.FACTORYNAME = #{shopName} AND C.OPER_DESC = #{operationName} AND C.PRODUCTIONTYPE = #{productionType} AND C.PRODUCTCATEGORY = #{productCategory} --AND C.DEFECTRATIOPK <=5 AND ( ( C.TIMEHH <= #{endTime} AND C.TIMEHH >= #{startTime})) GROUP BY C.OPERATORID, C.FACTORYNAME, C.OPER_DESC, C.PRODUCTSPECNAME, C.FACTORYDATE, C.TIMEHH, C.PRODUCTCATEGORY, C.PRODUCTIONTYPE ORDER BY OPERATORID) TT1 GROUP BY TT1.OPERATORID ORDER BY TT1.OPERATORID DESC) D WHERE B.OPERATORID = D.OPERATORID ORDER BY B.OPERATORID DESC) TT2, WEBMGR.DAS_MDL_OPERATOR_INFO OP WHERE TT2.OPERATORID = OP.OPERATORID(+) ORDER BY TT2.OUTQTY DESC

	SELECT *
  FROM WEBMGR.DAS_MDL_OPERATOR_INFO OP
 WHERE OP.OPERATORNAME = #{opNameId}

		SELECT A.TIMEHH,
       A.FACTORYNAME,
       A.EVENTUSER,
       A.YIELDTYPE,
       A.OPER_DESC,
       A.PRODUCTIONTYPE,
       A.PRODUCTCATEGORY,
       A.PRODUCTOKQTY,
       A.PRODUCTTOTALQTY,
       TRUNC ((A.PRODUCTOKQTY / A.PRODUCTTOTALQTY)*100, 2) AS YIELDRATIO
  FROM MDWMGR.DAS_OPYIELD_MWD A
 WHERE     1 = 1
       AND A.EVENTUSER = #{opName}
       AND A.FACTORYNAME = #{shopName}
       AND A.OPER_DESC = #{operationName}
       AND A.PRODUCTIONTYPE = #{productionType}
       AND A.PRODUCTCATEGORY = #{productCategory}
       AND A.TIMEHH >= #{startTime}
       AND A.TIMEHH <= #{endTime}
       AND A.YIELDTYPE = 'OPERATOR'

SELECT DISTINCT A.DESCRIPTION
  FROM MDWMGR.DAS_OPDEFECT_MWD A
        WHERE     1 = 1
         AND A.FACTORYNAME = #{shopName}
         AND A.OPERATORID = #{opName}
         AND A.OPER_DESC = #{operationName}
         AND A.PRODUCTIONTYPE = #{productionType}
         AND A.PRODUCTCATEGORY = #{productCategory}
         AND A.TIMEHH >= #{startTime}
         AND A.TIMEHH <= #{endTime}

		SELECT DISTINCT A.PRODUCTCATEGORY
  FROM MDWMGR.DSB_OQADPPMSUM_MWD A
 WHERE     A.LINENAME = #{lineName}
       AND A.PRODUCTIONTYPE = #{productionType}
       AND A.OQAOPERATIONNAME = #{operationName}
       AND A.FACTORYNAME = #{shopName}

SELECT DISTINCT A.DESCRIPTION FROM MDWMGR.DSB_OQADPPMSUM_MWD A
   WHERE A.LINENAME = #{lineName}
         AND A.PRODUCTIONTYPE = #{productionType}
         AND A.OQAOPERATIONNAME = #{operationName}
         AND A.FACTORYNAME = #{shopName}
         AND A.PRODUCTCATEGORY = #{productCategory}

SELECT * FROM MDWMGR.DSB_OQADPPMSUM_MWD A 
   WHERE A.LINENAME = #{lineName}
         AND A.PRODUCTIONTYPE = #{productionType}
         AND A.OQAOPERATIONNAME = #{operationName}
         AND A.FACTORYNAME = #{shopName}
         AND A.PRODUCTCATEGORY = #{productCategory}

	SELECT * FROM MDWMGR.DSB_OQALRRSUM_MWD A 
   WHERE A.LINENAME = #{lineName}
         AND A.PRODUCTIONTYPE = #{productionType}
         AND A.OQAOPERATIONNAME = #{operationName}
         AND A.FACTORYNAME = #{shopName}
         AND A.PRODUCTCATEGORY = #{productCategory}

	  SELECT DISTINCT A.DEFECTCODE
    FROM MDWMGR.DAS_MATOPDEFRECORD_MWD A
	   WHERE     A.DATATYPE = 'DEFECTRATIODPPM'
         AND A.MATERIALSPECNAME = #{productCategory}
         AND A.DESCRIPTION = #{operationName}
ORDER BY A.DEFECTCODE ASC

		SELECT *
  FROM WEBMGR.DAS_CGPLAN A
 WHERE A.PRODUCTCATEGORY = #{productCategory} AND A.IN_OUT = #{inoutType}
